import React, { useState, useEffect } from 'react';
import './App.css';
import axios from "axios";

import { API_URL,API_GET_URL } from "../src/constants";
function App() {
  const [inputValue, setInputValue] = useState('');
  const [predictions, setPredictions] = useState([]);
  const [text,setText]=useState('');
  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.post(API_GET_URL, { task: '0' });
        console.log(response)
        setText(response.data);
        console.log(text)
      } catch (error) {
        console.log(error);
      }
    }
    fetchData();
  }, []);

  useEffect(() => {
    // update the table values whenever predictions change
    const table = document.getElementById('predictions-table');
    if (table) {
      const rows = table.getElementsByTagName('tr');
      if (rows.length === 2) {
        const cells = rows[1].getElementsByTagName('td');
        cells[0].innerText = predictions['MCTR-R'];
        cells[1].innerText = predictions['MCTR-LM'];
        cells[2].innerText = predictions['Human'];
        cells[3].innerText = predictions['Consistent'];
      }
    }
  }, [predictions]);

  const handleSubmit = async () => {
    try {
      const response = await axios.post(API_URL, { input: inputValue });
      setPredictions(response.data.score);
      console.log(response)
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1>Headlines</h1>
      <h2>{text}</h2>
      <input className='input'
        type="text"
        value={inputValue}
        onChange={(event) => setInputValue(event.target.value)}
        placeholder="Enter some text here"
      />
      <button type='submit'onClick={handleSubmit}>Submit</button>
      <table id = "predictins-table" style={{ border: '1px solid black', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Headline</th>
            <th>Prediction 1</th>
            <th>Prediction 2</th>
            <th>Prediction 3</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>0</td>
            <td>0</td>
            <td>0</td>
            <td>0</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default App;
