import React, { useState, useEffect } from 'react';
import './App.css';
import Table from 'react-bootstrap/Table';
import axios from "axios";
import { Stack } from '@mui/material';
import { API_URL,API_GET_URL } from "../src/constants";
function App() {
  const [inputValue, setInputValue] = useState('');
  const [predictions, setPredictions] = useState([]);
  const [text,setText]=useState('');
  async function fetchData() {
    try {
      const response = await axios.post(API_GET_URL, { task: '0' });
      console.log(response)
      setText(response.data);
      console.log(text)
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    // update the table values whenever predictions change
    const table = document.getElementById('predictions-table');
    if (table) {
      const rows = table.getElementsByTagName('tr');
      if (rows.length === 2) {
        console.log('setting')
        const cells = rows[1].getElementsByTagName('td');
        cells[0].innerText = predictions['MCTR-P'];
        cells[1].innerText = predictions['MCTR-LM'];
        cells[2].innerText = predictions['Human'];
        cells[3].innerText = predictions['Consistent'];
      }
    }
  }, [predictions]);

  const handleSubmit = async () => {
    try {
      const response = await axios.post(API_URL, { input: inputValue });
      setInputValue('');
      console.log(response.data)
      setPredictions(response.data);

      console.log(response)
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div className='headline' style={{display:'flex',background:'#067', width:'100%',alignItems:'center',justifyContent:'center', alignContent:'center', height:'80px'}}>
        <h1 style={{color:'#FFF'}}>Headlines</h1>
      </div>
      <h4 style={{marginTop:"50px",fontFamily:'monospace'}}>Reframe the below headline to find your score</h4>
      <h2 style={{margin:"50px",fontFamily:'monospace'}}>{text}</h2>
      <Stack direction='row' sx={{width:'100%', alignItems:'center', justifyContent:"center", margin:'10px'}}>
        <input className='input'
          type="text"
          value={inputValue}
          onChange={(event) => setInputValue(event.target.value)}
          placeholder="Enter some text here"
        />
        <button style={{marginLeft:'10px'}} type='submit'onClick={handleSubmit}>Submit</button>
      </Stack>
      <Table sx={{margin:'30px'}} striped bordered id = "predictions-table">
        <thead>
          <tr>
            <th>MCTR-P</th>
            <th>MCTR-LM</th>
            <th>Human</th>
            <th>Consistent</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>0</td>
            <td>0</td>
            <td>0</td>
            <td>0</td>
          </tr>
        </tbody>
      </Table>
      <button style={{marginTop:'10px'}} onClick={fetchData}>Tap for new headline</button>
    </div>
  );
}

export default App;
