import React, { useState, useEffect } from 'react';
import './App.css';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import axios from "axios";
import { Stack } from '@mui/material';
import { API_URL,API_GET_URL } from "../src/constants";
function App() {
  const [inputValue, setInputValue] = useState('');
  const [predictions, setPredictions] = useState([]);
  const [text,setText]=useState('');
  const [isTabActive, setIsTabActive] = useState(true);
  const [tabChangeCount, setTabChangeCount] = useState(0);
  const [count,setCount] =useState(0);
  const [sortedPredictions, setSortedPredictions]=useState([]);
  // Function to handle tab visibility change
  const handleVisibilityChange = () => {
    setIsTabActive(!document.hidden);
    setTabChangeCount((prevCount) => prevCount + 1);
    console.log('count')
    console.log(tabChangeCount)
  };


  async function fetchData() {
    try {
      const response = await axios.post(API_GET_URL, { task: '0' });
      setText(response.data);
    } catch (error) {
      console.log(error);
    }
  }
  useEffect(() => {
    fetchData();
    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, []);

  const clearTableRows = () => {
    const tableBody = document.getElementById('table-body');
    console.log('in clear')
    console.log(tableBody)
    if (tableBody) {
      const rows = tableBody.querySelectorAll('tr');

      console.log('in body')
      console.log(rows)
      rows.forEach((row) => {
        row.remove();

      });
      console.log(rows)
    }

  };

  useEffect(() => {
    const table = document.getElementById('predictions-table');
    if (table) {
      // Clear existing rows
      // table.querySelectorAll('tbody tr').forEach((row) => row.remove());
      console.log("Before clearing table rows");
      clearTableRows();
      console.log("after clearing table rows");
      // Sort predictions array by score in descending order
      const sp= predictions.sort((a, b) => b.Score - a.Score);
      setSortedPredictions(sp)
      // Create new rows for each prediction
      sortedPredictions.forEach((prediction) => {
        const newRow = table.insertRow();

        // Create cells and set their values
        const headlineCell = newRow.insertCell();
        headlineCell.innerText = prediction.Headline;

        const scoreCell = newRow.insertCell();
        scoreCell.innerText = prediction.Score;
        if (prediction.Headline === inputValue) {
          newRow.classList.add('green-row');
        }
        setInputValue('');
        if (predictions.length >= 4) {
          console.log('done');
          // fetchData()
          window.location.reload()
          setCount((prevCount)=>prevCount+1)
          if (count==2){
            console.log('over')
              alert('Game over')
          }
        }
      });
    }
  }, [predictions]);



  const handleSubmit = async () => {
    try {
      if (inputValue.length > 100) {
        alert("Your proposed headline is too long.Try to shorten it for good score!!");
      }
      const response = await axios.post(API_URL, { input: inputValue });
      // setPredictions(response.data);
      const newPred = { Headline: response.data.Headline, Score: response.data.Score };
      //setPredictions([{ Headline: response.data.Headline, Score: response.data.Score }]);
      setPredictions((prevPredictions) => [...prevPredictions, newPred]);
      console.log(predictions)
      // if (predictions.length >= 8) {
      //   console.log('done');
      //   // fetchData()
      //   window.location.reload()
      // }
      if(response.data.Consistent < 0.5){
        alert("You are moving off the topic.Try to be consistent to get a good score!!");
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <div className='headline' style={{display:'flex',background:'green', width:'100%',alignItems:'center',justifyContent:'center', alignContent:'center', height:'80px'}}>
        <h1 style={{color:'#FFF',fontFamily:'monospace'}}>Headlines</h1>
      </div>
      <h4 style={{marginTop:"50px",fontFamily:'monospace'}}>Reframe the below headline to find your score</h4>
      <h2 style={{margin:"50px",fontFamily:'monospace'}}>{text}</h2>
      <Stack direction='row' sx={{width:'100%', alignItems:'center', justifyContent:"center", margin:'10px'}}>
        <input className='input'
          type="text"
          value={inputValue}
          onChange={(event) => setInputValue(event.target.value)}
          placeholder="Enter your proposed headline"
        />
        <Button variant="success" style={{marginLeft:'10px', top:'-10px', position:'relative'}} type='submit'onClick={handleSubmit}>Submit</Button>
      </Stack>
      <Table  striped bordered id = "predictions-table">
        <thead>
          <tr>
            <th>Headline</th>
            <th>Score</th>
          </tr>
        </thead>
        <tbody id='table-body'>
          <tr>
            <td>0</td>
            <td>0</td>  
          </tr>
          {/* {sortedPredictions.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
            </tr>
          ))} */}
        </tbody>
      </Table>
    </div>
  );
}

export default App;
